/* eslint-disable jsx-a11y/alt-text */
import React from "react";
import { Link } from "react-router-dom";
import PortfolioOne from "../portfolio/PortfolioOne";
import { Col } from "react-bootstrap";
import data from "../../data/burc.json";
import { SRLWrapper } from "simple-react-lightbox";
import Spacer from "../common/Spacer";

function Burc() {
  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  };
  return (
    <SRLWrapper>
      <>
        <section id="departments" className="departments">
          <Spacer />
          <div className="container">
            <div className="section-title">
              <h2>ÜRÜNLERİMİZ</h2>
            </div>

            <div className="row gy-4">
              <div className="col-lg-3">
                <ul className="nav nav-tabs flex-column">
                  <li className="nav-item">
                    <Link
                      className="nav-link"
                      to="/civata"
                      onClick={scrollToTop}
                    >
                      Özel Civata Ürünleri
                    </Link>
                  </li>
                  <li className="nav-item">
                    <Link className="nav-link" to="/pim" onClick={scrollToTop}>
                      Özel Üretim Pimler
                    </Link>
                  </li>
                  <li className="nav-item">
                    <Link
                      className="nav-link"
                      to="/pirinc"
                      onClick={scrollToTop}
                    >
                      Özel Üretim Pirinç Parçalar
                    </Link>
                  </li>
                  <li className="nav-item">
                    <Link
                      className="nav-link active show"
                      to="/burc"
                      onClick={scrollToTop}
                    >
                      Özel Üretim Burçlar
                    </Link>
                  </li>
                </ul>
              </div>
              <div className="col-lg-9">
                <div className="tab-content">
                  <div className="tab-pane active show" id="tab-1">
                    <div className="row gy-4">
                      {data.map((burc) => {
                        const { picture, title, desc, nav, id } = burc;
                        return (
                          <Col md={6} key={id} className="text-center mb-5">
                            <PortfolioOne
                              picture={picture}
                              title={title}
                              desc={desc}
                              nav={nav}
                            />
                          </Col>
                        );
                      })}
                      <Col md={6} className="text mb-5">
                        <div className="portfolio-description">
                          <h2>Metal Ürün</h2>
                          <p>
                            Lorem Ipsum Nedir? Lorem Ipsum, dizgi ve baskı
                            endüstrisinde kullanılan mıgır metinlerdir. Lorem
                            Ipsum, adı bilinmeyen bir matbaacının bir hurufat
                            numune kitabı oluşturmak üzere bir yazı galerisini
                            alarak karıştırdığı 1500'lerden beri endüstri
                            standardı sahte metinler olarak kullanılmıştır.
                            Beşyüz yıl boyunca varlığını sürdürmekle kalmamış,
                            aynı zamanda pek değişmeden elektronik dizgiye de
                            sıçramıştır. 1960'larda Lorem Ipsum pasajları da
                            içeren Letraset yapraklarının yayınlanması ile ve
                            yakın zamanda Aldus PageMaker gibi Lorem Ipsum
                            sürümleri içeren masaüstü yayıncılık yazılımları ile
                            popüler olmuştur.
                          </p>
                        </div>
                      </Col>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>
      </>
    </SRLWrapper>
  );
}

export default Burc;
