import React from "react";
import { Link } from "react-router-dom";

function Metal2() {
  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  };
  return (
    <section id="departments" className="departments">
          <div className="container">
            <div className="section-title">
              <h2>ÜRÜNLERİMİZ</h2>
            </div>

            <div className="row gy-4">
              <div className="col-lg-12">
                <ul className="nav nav-tabs flex-direction justify-content-center">
                  {/*<li className="nav-item nav-link">
                      KATEGORİLER
                  </li>
                  <hr/> */}
                  <li className="nav-item">
                    <Link
                      className="nav-link active show"
                      to="/Vidalar"
                      onClick={scrollToTop}
                    >
                      Vidalar
                    </Link>
                  </li>
                  <li className="nav-item">
                    <Link
                      className="nav-link"
                      to="/Pimler"
                      onClick={scrollToTop}
                    >
                      Pimler
                    </Link>
                  </li>
                  <li className="nav-item">
                    <Link
                      className="nav-link"
                      to="/Somunlar"
                      onClick={scrollToTop}
                    >
                      Somunlar
                    </Link>
                  </li>

                </ul>
              </div>
              <div className="col-lg-12">
                <div className="tab-content">
                  <div className="tab-pane active show" id="tab-1">
                    <div className="row gy-4">
                      {data.map((product) => {
                        const { picture, title, desc, nav, id } = product;
                        return (
                          <Col md={3} key={id} className="text-center mb-5">
                            <PortfolioOne
                              picture={picture}
                              title={title}
                              desc={desc}
                              nav={nav}
                            />
                          </Col>
                        );
                      })}
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>
  );
}

export default Metal2;
