/* eslint-disable react/jsx-no-target-blank */
/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState } from "react";
import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import NavDropdown from "react-bootstrap/NavDropdown";

import { Link } from "react-router-dom";

function Header() {
  const [expanded, setExpanded] = useState(false);

  const toggleNav = () => {
    setExpanded(expanded ? false : "expanded");
  };

  const handleClick = () => {
    window.scrollTo(0, 0);
  };

  return (
    <Navbar
      id="header"
      sticky="top"
      expand="lg"
      expanded={expanded}
      collapseOnSelect
    >
      <Container className="container d-flex align-items-center justify-content-center">
        <Link to="/" className="logo">
          <img
            src="assets/img/Aypinautomat/aypin 1.webp"
            alt="Siyah Logo"
            className="img-fluid"
          />
        </Link>
        <h1 className="logo me-auto">
          <Link to="/"></Link>
        </h1>

        <Navbar.Toggle
          onClick={() => {
            toggleNav();
          }}
          aria-controls="basic-navbar-nav"
        />
        <Navbar.Collapse
          expanded={expanded}
          className="justify-content-center"
          id="basic-navbar-nav"
        >
          <Nav className="ms-auto" id="navbar">
            <Nav.Link
              as={Link}
              to="/"
              onClick={() => {
                toggleNav();
                handleClick();
              }}
              className="nav-link scrollto active"
            >
              Anasayfa
            </Nav.Link>
            <Nav.Link
              as={Link}
              to="/aboutus"
              className="nav-link scrollto"
              onClick={() => {
                toggleNav();
                handleClick();
              }}
            >
              Hakkımızda
            </Nav.Link>

            <NavDropdown
              title="Ürünlerimiz"
              id="collasible-nav-dropdown"
              class="nav-link scroolto"
            >
              <NavDropdown.Item
                as={Link}
                to="/"
                onClick={() => {
                  toggleNav();
                  handleClick();
                }}
              >
                Ürün 1
              </NavDropdown.Item>
              <NavDropdown.Item
                as={Link}
                to="/"
                onClick={() => {
                  toggleNav();
                  handleClick();
                }}
              >
                Ürün 2
              </NavDropdown.Item>
              <NavDropdown.Item
                as={Link}
                to="/"
                onClick={() => {
                  toggleNav();
                  handleClick();
                }}
              >
                Ürün 3
              </NavDropdown.Item>
            </NavDropdown>

            <Nav.Link
              as={Link}
              to="/contact"
              className="nav-link scrollto"
              onClick={() => {
                toggleNav();
                handleClick();
              }}
            >
              İletişim
            </Nav.Link>
            <Nav.Link
              href="https://wa.me/905321621647"
              title="Bize Whatsapptan Ulaşın"
              target={"_blank"}
              className="social-links"
            >
              <i class="bx bxl-whatsapp" style={{ fontSize: "24px" }}></i>
            </Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default Header;
