import React from "react";
import { useTranslation } from "react-i18next";
import { Link } from "react-router-dom";

function ProductOne(props) {
  const { t } = useTranslation();
  const { picture, title, desc, nav, id } = props;

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  };
  return (
    <>
      <div className="card border-0">
        <div className="card-body">
          <Link as={Link} to={props.picture}>
            <img
              src={props.picture}
              className="figure-img1 img-fluid rounded-0 shadow"
              alt="..."
            />
          </Link>
          <Link
            as={Link}
            to={props.picture}
            className="card-title text-dark"
            data-fslightbox="gallery"
          >
            <h6 className="card-title text-center">{props.title}</h6>
          </Link>
        </div>
      </div>

      <div className="product__item">
        <div className="product__item__img">
          <img src={props.pictur} alt={props.title} />
          <div className="product__item__btn">
            <a className="img-popup" href={props.picture}>
              <i className="fas fa-eye"></i>
            </a>
          </div>
          <div className="product__item__content">
            <div className="product__item__ratings">
              <span className="fa fa-star"></span>
              <span className="fa fa-star"></span>
              <span className="fa fa-star"></span>
              <span className="fa fa-star"></span>
              <span className="fa fa-star"></span>
            </div>
            <h4 className="product__item__title">
              <a href="product-details.html">{props.title}</a>
            </h4>
            <a href="product-details.html" className="nisoz-btn">
              <span className="nisoz-btn__shape"></span>
              <span className="nisoz-btn__shape"></span>
              <span className="nisoz-btn__shape"></span>
              <span className="nisoz-btn__shape"></span>
              <span className="nisoz-btn__text">{props.price}</span>
            </a>
          </div>
        </div>
      </div>
    </>
  );
}

export default ProductOne;
