/* eslint-disable jsx-a11y/anchor-is-valid */
/* eslint-disable jsx-a11y/no-redundant-roles */
/* eslint-disable jsx-a11y/iframe-has-title */
import React, { useRef } from 'react';
import emailjs from 'emailjs-com';
import Spacer from "../common/Spacer";

function Contact() {
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm('service_56g7q46', 'template_giqb4o4', form.current, '7LseggvnGoIcgGHia')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
  };

  return (
    <form
                action=""
                method="post"
                role="form"
                class="php-email-form"
                ref={form}
                onSubmit={sendEmail}
              >
                <div class="row">
                  <div class="col-md-6 form-group">
                    <input
                      type="text"
                      name="user_name"
                      id="gonder_isim6"
                      class="form-control"
                      placeholder="Adınız"
                      required
                    />
                  </div>
                  <div class="col-md-6 form-group mt-3 mt-md-0">
                    <input
                      type="email"
                      class="form-control"
                      name="user_email"
                      id="gonder_mail2"
                      placeholder="Emailiniz"
                      required
                    />
                  </div>
                </div>
                <div class="form-group mt-3">
                  <input
                    type="text"
                    class="form-control"
                    name="baslik"
                    id="baslik"
                    placeholder="Konu"
                    required
                  />
                </div>
                <div class="form-group mt-3">
                  <textarea
                    class="form-control"
                    name="message" 
                    id="textarea" 
                    cols="40"
                    rows="5"
                    placeholder="Mesajınız"
                    required
                  ></textarea>
                </div>
                <div class="my-3">
                  <div class="loading">Bekleyiniz</div>
                  <div class="error-message"></div>
                  <div class="sent-message">
                    Mesajınız gönderildi. Teşekkür ederiz!
                  </div>
                </div>
                <div class="text-center">
                  <button type="submit" onSubmit={sendEmail}>Gönder</button>
                </div>
              </form>
  );
}

export default Contact;
