import React, { useState } from "react";
import { Container, Nav, Navbar, NavDropdown } from "react-bootstrap";
import { Link } from "react-router-dom";
import { useTranslation } from "react-i18next";

function Header() {
  const [expanded, setExpanded] = useState(false);

  const toggleNav = () => {
    setExpanded(!expanded);
  };

  const handleClick = () => {
    window.scrollTo(0, 0);
    setExpanded(false);
  };

  const { t, i18n } = useTranslation();

  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
  };

  return (
    <>
      <Navbar
        id="header"
        className="fixed-top header-inner-pages"
        expand="lg"
        expanded={expanded}
      >
        <div className="main-header-three__top">
          <div className="main-header-three__top-inner">
            <div className="main-header-three__top-left">
              <ul className="list-unstyled main-header-three__contact-list">
                <li>
                  <div className="icon">
                    <i className="fas fa-envelope"></i>
                  </div>
                  <div className="text">
                    <p>
                      <a href="mailto:needhelp@company.com">
                        needhelp@company.com
                      </a>
                    </p>
                  </div>
                </li>
                <li>
                  <div className="icon">
                    <i className="fas fa-map-marker"></i>
                  </div>
                  <div className="text">
                    <p>30 Broklyn Golden Street. New York</p>
                  </div>
                </li>
              </ul>
            </div>
            <div className="main-header-three__top-right">
              <ul className="list-unstyled main-header-three__top-menu">
                <li>
                  <Link to="/about">Hakkımızda</Link>
                </li>
                <li>
                  <Link to="/contact">İletişim</Link>
                </li>
              </ul>
              <div className="main-header-three__social-box">
                <h4 className="main-header-three__social-title">Follow on:</h4>
                <div className="main-header-three__social">
                  <a href="#">
                    <i className="fab fa-twitter"></i>
                  </a>
                  <a href="#">
                    <i className="fab fa-facebook"></i>
                  </a>
                  <a href="#">
                    <i className="fab fa-pinterest-p"></i>
                  </a>
                  <a href="#">
                    <i className="fab fa-instagram"></i>
                  </a>
                </div>
              </div>
            </div>
          </div>
        </div>

        <Container className="container d-flex align-items-center justify-content-center">
          <Link to="/" className="logo" onClick={handleClick}>
            <img
              src="assets/img/Aypinautomat/aypin 1.webp"
              alt="Siyah Logo"
              className="img-fluid"
            />
          </Link>
          <h1 className="logo me-auto">
            <Link to="/"></Link>
          </h1>
          <Navbar.Toggle onClick={toggleNav} aria-controls="basic-navbar-nav" />
          <Navbar.Collapse
            expanded={expanded}
            className="justify-content-center"
            id="basic-navbar-nav"
          >
            <Nav className="ms-auto flex-column flex-lg-row" id="navbar">
              <Nav.Link
                as={Link}
                to="/"
                onClick={handleClick}
                className="nav-link scrollto active"
              >
                {t("Ana Sayfa")}
              </Nav.Link>
              <Nav.Link
                as={Link}
                to="/aboutus"
                className="nav-link scrollto"
                onClick={handleClick}
              >
                {t("Hakkımızda")}
              </Nav.Link>

              <NavDropdown
                title={t("Ürünlerimiz")}
                id="collasible-nav-dropdown"
                class="nav-link scroolto"
              >
                <NavDropdown.Item as={Link} to="/civata" onClick={handleClick}>
                  {t("Özel Üretim Civatalar")}
                </NavDropdown.Item>
                <NavDropdown.Item as={Link} to="/pim" onClick={handleClick}>
                  {t("Özel Üretim Pimler")}
                </NavDropdown.Item>
                <NavDropdown.Item as={Link} to="/pirinc" onClick={handleClick}>
                  {t("Özel Üretim Pirinç Parçalar")}
                </NavDropdown.Item>
                <NavDropdown.Item as={Link} to="/burc" onClick={handleClick}>
                  {t("Özel Üretim Burçlar")}
                </NavDropdown.Item>
              </NavDropdown>

              <Nav.Link
                as={Link}
                to="/product"
                className="nav-link scrollto"
                onClick={handleClick}
              >
                {t("Üretim")}
              </Nav.Link>

              <Nav.Link
                as={Link}
                to="/contact"
                className="nav-link scrollto"
                onClick={handleClick}
              >
                {t("İletişim")}
              </Nav.Link>
              <Nav.Link
                onClick={() => {
                  changeLanguage("tr");
                  handleClick();
                }}
              >
                {t("TR")}&nbsp;
                <img
                  src="/assets/img/TR Bayragi.webp"
                  alt=""
                  width="20"
                  height="17"
                />
              </Nav.Link>
              <Nav.Link
                onClick={() => {
                  changeLanguage("en");
                  handleClick();
                }}
              >
                {t("EN")}&nbsp;
                <img
                  src="/assets/img/EN Bayragi.webp"
                  alt=""
                  width="20"
                  height="17"
                />
              </Nav.Link>
              <Nav.Link
                href="https://wa.me/905321621647"
                title={t("Bize Whatsapptan Ulaşın")}
                target={"_blank"}
                className="social-links"
              >
                <i className="bx bxl-whatsapp" style={{ fontSize: "24px" }}></i>
              </Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </>
  );
}

export default Header;
