import React from "react";
import { SRLWrapper } from "simple-react-lightbox";
import { useTranslation } from "react-i18next";
import { Link } from "react-router-dom";

function Civata() {
  const { t } = useTranslation();

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  };

  return (
    <SRLWrapper>
      <>
        <section id="departments" className="departments">
          <div className="container">
            <div className="section-title">
              <h2>ÜRÜNLERİMİZ</h2>
            </div>

            <div className="row gy-4">
              <div className="col-lg-3">
                <ul className="nav nav-tabs flex-column">
                  <li className="nav-item">
                    <Link
                      className="nav-link"
                      to="/civata"
                      onClick={scrollToTop}
                    >
                      Özel Civata Ürünleri
                    </Link>
                  </li>
                  <li className="nav-item">
                    <Link
                      className="nav-link active show"
                      to="/pim"
                      onClick={scrollToTop}
                    >
                      Özel Üretim Pimler
                    </Link>
                  </li>
                  <li className="nav-item">
                    <Link
                      className="nav-link"
                      to="/pirinc"
                      onClick={scrollToTop}
                    >
                      Özel Üretim Pirinç Parçalar
                    </Link>
                  </li>
                  <li className="nav-item">
                    <Link
                      className="nav-link"
                      to="/burc"
                      onClick={scrollToTop}
                    >
                      Özel Üretim Burçlar
                    </Link>
                  </li>
                </ul>
              </div>
              <div className="col-lg-9">
                <div className="tab-content">
                  <div className="tab-pane active show" id="tab-1">
                    <div className="row gy-4">
                      {data.map((pim) => {
                        const { picture, title, desc, nav, id } = pim;
                        return (
                          <Col md={6} key={id} className="text-center mb-5">
                            <PortfolioOne
                              picture={picture}
                              title={title}
                              desc={desc}
                              nav={nav}
                            />
                          </Col>
                        );
                      })}
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>
      </>
    </SRLWrapper>
  );
}

export default Civata;
