import React, { useState } from "react";

function AccordQuestionion() {
  // Başlık ve içerikleri bir arada tutmak için bir state kullanın
  const [activeIndex, setActiveIndex] = useState(null);

  // Başlığa tıklandığında durumu değiştirin ve içeriği açın/kapatın
  const toggleAccordion = (index) => {
    if (activeIndex === index) {
      // Eğer zaten açıksa, kapatın
      setActiveIndex(null);
    } else {
      // Değilse, ilgili içeriği açın
      setActiveIndex(index);
    }
  };

  // Akordion içeriğini oluşturun
  const accordionItems = [
    {
      title: "Başlık 1",
      content: "İçerik 1",
    },
    {
      title: "Başlık 2",
      content: "İçerik 2",
    },
    {
      title: "Başlık 3",
      content: "İçerik 3",
    },
  ];

  return (
    <div className="accordion">
      {accordionItems.map((item, index) => (
        <div className="accordion-item" key={index}>
          <div
            className={`accordion-title ${activeIndex === index ? "active" : ""}`}
            onClick={() => toggleAccordion(index)}
          >
            {item.title}
          </div>
          {activeIndex === index && (
            <div className="accordion-content">{item.content}</div>
          )}
        </div>
      ))}
    </div>
  );
}

export default Question;
