import React, { useState } from "react";
import { Container, Nav, Navbar, NavDropdown } from "react-bootstrap";
import { Link } from "react-router-dom";
import { useTranslation } from "react-i18next";
import TopBar from "./TopBar";

function Header() {
  const [expanded, setExpanded] = useState(false);

  const toggleNav = () => {
    setExpanded(!expanded);
  };

  const handleClick = () => {
    window.scrollTo(0, 0);
  };

  const { t, i18n } = useTranslation();

  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
  };

  return (
    <>
    <TopBar/>
    <Navbar
      id="header"
      className="fixed-top header-inner-pages"
      expand="lg"
      expanded={expanded}
    >
      <Container className="container d-flex align-items-center justify-content-center">
        <Link
          to="/"
          className="logo"
          onClick={() => {
            toggleNav();
            handleClick();
          }}
        >
          <img
            src="assets/img/Aypinautomat/aypin 1.webp"
            alt="Siyah Logo"
            className="img-fluid"
          />
        </Link>
        <h1 className="logo me-auto">
          <Link to="/"></Link>
        </h1>
        <Navbar.Toggle
          onClick={() => {
            toggleNav();
          }}
          aria-controls="basic-navbar-nav"
        />
        <Navbar.Collapse
          expanded={expanded}
          className="justify-content-center"
          id="basic-navbar-nav"
        >
          <Nav className="ms-auto" id="navbar">
            <Nav.Link
              as={Link}
              to="/"
              onClick={() => {
                toggleNav();
                handleClick();
              }}
              className="nav-link scrollto active"
            >
              {t("Ana Sayfa")}
            </Nav.Link>
            <Nav.Link
              as={Link}
              to="/aboutus"
              className="nav-link scrollto"
              onClick={() => {
                toggleNav();
                handleClick();
              }}
            >
              {t("Hakkımızda")}
            </Nav.Link>

            <NavDropdown
              title={t("Ürünlerimiz")}
              id="collasible-nav-dropdown"
              class="nav-link scroolto"
            >
              <NavDropdown.Item
                as={Link}
                to="/civata"
                onClick={() => {
                  toggleNav();
                  handleClick();
                }}
              >
                {t("Özel Üretim Civatalar")}
              </NavDropdown.Item>
              <NavDropdown.Item
                as={Link}
                to="/pim"
                onClick={() => {
                  toggleNav();
                  handleClick();
                }}
              >
                {t("Özel Üretim Pimler")}
              </NavDropdown.Item>
              <NavDropdown.Item
                as={Link}
                to="/pirinc"
                onClick={() => {
                  toggleNav();
                  handleClick();
                }}
              >
                {t("Özel Üretim Pirinç Parçalar")}
              </NavDropdown.Item>
              <NavDropdown.Item
                as={Link}
                to="/burc"
                onClick={() => {
                  toggleNav();
                  handleClick();
                }}
              >
                {t("Özel Üretim Burçlar")}
              </NavDropdown.Item>
            </NavDropdown>

            <Nav.Link
              as={Link}
              to="/product"
              className="nav-link scrollto"
              onClick={() => {
                toggleNav();
                handleClick();
              }}
            >
              {t("Üretim")}
            </Nav.Link>

            <Nav.Link
              as={Link}
              to="/contact"
              className="nav-link scrollto"
              onClick={() => {
                toggleNav();
                handleClick();
              }}
            >
              {t("İletişim")}
            </Nav.Link>
            <Nav.Link
              onClick={() => {
                changeLanguage("tr");
                toggleNav();
                handleClick();
              }}
            >
              {t("TR")}&nbsp;
              <img
                src="/assets/img/TR Bayragi.webp"
                alt=""
                width="20"
                height="17"
              />
            </Nav.Link>
            <Nav.Link onClick={() => {
                changeLanguage("en");
                toggleNav();
                handleClick();
              }}>
              {t("EN")}&nbsp;
              <img
                src="/assets/img/EN Bayragi.webp"
                alt=""
                width="20"
                height="17"
              />
            </Nav.Link>
            <Nav.Link
              href="https://wa.me/905321621647"
              title={t("Bize Whatsapptan Ulaşın")}
              target={"_blank"}
              className="social-links"
            >
              <i class="bx bxl-whatsapp" style={{ fontSize: "24px" }}></i>
            </Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
    </>
  );
}

export default Header;
