/* eslint-disable jsx-a11y/alt-text */
import React from "react";
import { Link } from "react-router-dom";
import PortfolioOne from "../portfolio/PortfolioOne";
import { Col } from "react-bootstrap";
import data from "../../data/product.json";

function Education() {
  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  };

  return (
    
    <>
    <section id="departments" className="departments">
      <div className="container">
        <div className="section-title">
          <h2>EĞİTİMLERİMİZ</h2>
        </div>

        <div className="row gy-4">
          <div className="col-lg-3">
            <ul className="nav nav-tabs flex-column">
              <li className="nav-item">
                <Link
                  className="nav-link active show"
                  to="/EgitimMicroblading"
                  onClick={scrollToTop}
                >
                  Microblading ve Pudralama Tekniği Eğitimi
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  className="nav-link"
                  to="/EgitimEyelinerDipliner"
                  onClick={scrollToTop}
                >
                  Kalıcı Eyeliner - Dipliner Eğitimi
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  className="nav-link"
                  to="/EgitimDudakRenklendirme"
                  onClick={scrollToTop}
                >
                  Kalıcı Dudak Renklendirme Eğitimi
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  className="nav-link"
                  to="/EgitimKasSilme"
                  onClick={scrollToTop}
                >
                  Kaş Silme Eğitimi
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  className="nav-link"
                  to="/EgitimKasKirpikLifting"
                  onClick={scrollToTop}
                >
                  Kaş - Kirpik Lifting Eğitimi
                </Link>
              </li>
            </ul>
          </div>
          <div className="col-lg-9">
            <div className="tab-content">
              <div className="tab-pane active show" id="tab-1">
                <div className="row gy-4">
                  {data.map((product) => {
                    const { picture, title, desc, nav, id } = product;
                    return (
                      <Col md={4} key={id} className="text-center mb-5">
                        <PortfolioOne
                          picture={picture}
                          title={title}
                          desc={desc}
                          nav={nav}
                        />
                      </Col>
                    );
                  })}
                </div>
              </div>
              <div className="tab-pane" id="tab-2">
                <div className="row gy-4">
                {data.map((product) => {
                    const { picture, title, desc, nav, id } = product;
                    return (
                      <Col md={4} key={id} className="text-center mb-5">
                        <PortfolioOne
                          picture={picture}
                          title={title}
                          desc={desc}
                          nav={nav}
                        />
                      </Col>
                    );
                  })}
                </div>
              </div>
              <div className="tab-pane" id="tab-3">
                <div className="row gy-4">
                {data.map((product) => {
                    const { picture, title, desc, nav, id } = product;
                    return (
                      <Col md={4} key={id} className="text-center mb-5">
                        <PortfolioOne
                          picture={picture}
                          title={title}
                          desc={desc}
                          nav={nav}
                        />
                      </Col>
                    );
                  })}
                </div>
              </div>
              <div className="tab-pane" id="tab-4">
              <div className="row gy-4">
                {data.map((product) => {
                    const { picture, title, desc, nav, id } = product;
                    return (
                      <Col md={4} key={id} className="text-center mb-5">
                        <PortfolioOne
                          picture={picture}
                          title={title}
                          desc={desc}
                          nav={nav}
                        />
                      </Col>
                    );
                  })}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
    </>
  );
}

export default Education;
