import React, { useState } from "react";
import { Button, Container, Nav, Navbar, NavDropdown } from "react-bootstrap";
import { Link } from "react-router-dom";
import { useTranslation } from "react-i18next";

function Header() {
  const { t, i18n } = useTranslation();
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };
  const [expanded, setExpanded] = useState(false);

  const toggleNav = () => {
    setExpanded(!expanded);
  };

  const handleClick = () => {
    window.scrollTo(0, 0);
    setExpanded(false);
  };

  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
  };

  return (
    <>
      <header className="main-header sticky-top fixed-top">
        <div
          className="topbar fixed-top sticky-top"
          style={{ backgroundColor: "#161617", padding: "10px" }}
        >
          <Container>
            <div className="d-flex justify-content-between align-items-center">
              <div className="d-flex align-items-center">
                <span className="topbar__wrapper">
                  <ul className="list-unstyled topbar__list">
                    <li>
                      <span className="fas fa-envelope"></span>
                      <a href="mailto:info@bkbsolarlight.com">
                        info@bkbsolarlight.com
                      </a>
                    </li>
                  </ul>
                </span>
              </div>
              <div className="d-flex">
                <Button
                  style={{
                    backgroundColor: "#f9d21c",
                    padding: "3px 7px 3px 7px",
                    fontSize: "13px",
                  }}
                  onClick={() => changeLanguage("tr")}
                >
                  TR
                </Button>
                &nbsp;
                <Button
                  style={{
                    backgroundColor: "#f9d21c",
                    padding: "3px 7px 3px 7px",
                    fontSize: "13px",
                  }}
                  onClick={() => changeLanguage("en")}
                >
                  EN
                </Button>
                &nbsp;
                <Button
                  style={{
                    backgroundColor: "#f9d21c",
                    padding: "3px",
                    fontSize: "13px",
                  }}
                  onClick={() => changeLanguage("ar")}
                >
                  عربي
                </Button>
              </div>
            </div>
          </Container>
        </div>
        <Navbar
          collapseOnSelect
          expand="lg"
          bg="light"
          variant="light"
          className="main-menu pt-2"
          expanded={expanded}
        >
          <Container fluid>
            <Navbar.Brand>
              <Link to="/" className="main-menu__logo" onClick={handleClick}>
                <img
                  src="assets/images/favicons/250x150.png"
                  width="126"
                  height="84"
                  alt="BKB"
                />
              </Link>
            </Navbar.Brand>

            <Navbar.Toggle
              onClick={toggleNav}
              aria-controls="basic-navbar-nav"
            />
            <Navbar.Collapse
              expanded={expanded}
              className="justify-content-center"
              id="basic-navbar-nav"
            >
              <Nav className="ms-auto">
                <Nav.Link
                  as={Link}
                  to="/"
                  onClick={handleClick}
                  className="nav-link scrollto active"
                >
                  {t("Ana Sayfa")}
                </Nav.Link>
                <Nav.Link
                  as={Link}
                  to="/aboutus"
                  className="nav-link scrollto"
                  onClick={handleClick}
                >
                  {t("Hakkımızda")}
                </Nav.Link>

                <NavDropdown
                  title={t("Ürünlerimiz")}
                  id="collasible-nav-dropdown"
                  className="nav-link scroolto"
                >
                  <NavDropdown.Item
                    as={Link}
                    to="/civata"
                    onClick={handleClick}
                  >
                    {t("Özel Üretim Civatalar")}
                  </NavDropdown.Item>
                  <NavDropdown.Item as={Link} to="/pim" onClick={handleClick}>
                    {t("Özel Üretim Pimler")}
                  </NavDropdown.Item>
                  <NavDropdown.Item
                    as={Link}
                    to="/pirinc"
                    onClick={handleClick}
                  >
                    {t("Özel Üretim Pirinç Parçalar")}
                  </NavDropdown.Item>
                  <NavDropdown.Item as={Link} to="/burc" onClick={handleClick}>
                    {t("Özel Üretim Burçlar")}
                  </NavDropdown.Item>
                </NavDropdown>

                <Nav.Link
                  as={Link}
                  to="/product"
                  className="nav-link scrollto"
                  onClick={handleClick}
                >
                  {t("Üretim")}
                </Nav.Link>

                <Nav.Link
                  as={Link}
                  to="/contact"
                  className="nav-link scrollto"
                  onClick={handleClick}
                >
                  {t("İletişim")}
                </Nav.Link>
                <Nav.Link
                  href="https://wa.me/905321621647"
                  title={t("Bize Whatsapptan Ulaşın")}
                  target={"_blank"}
                  className="social-links"
                >
                  <i
                    className="fab fa-whatsapp"
                    style={{ fontSize: "24px" }}
                  ></i>
                </Nav.Link>
              </Nav>
              <div class="main-menu__right">
                <a href="tel:+02242116266" class="main-menu__phone">
                  <i class="icon-telephone"></i>
                </a>
                <a
                  href="https://www.facebook.com/profile.php?id=100094870608591"
                  target="_blank"
                  class="main-menu__phone"
                >
                  <i class="fab fa-facebook"></i>
                </a>
              </div>
            </Navbar.Collapse>
          </Container>
        </Navbar>
      </header>
    </>
  );
}

export default Header;
