import React, { useEffect } from "react";
import Carousel from "react-bootstrap/Carousel";

function Slider() {
  useEffect(() => {
    const heroCarouselIndicators = document.querySelector(
      "#heroCarouselIndicators"
    );
    const heroCarouselItems = document.querySelectorAll(
      "#heroCarousel .carousel-item"
    );

    heroCarouselItems.forEach((item, index) => {
      const indicator = document.createElement("li");
      indicator.classList.add("carousel-indicator");
      indicator.setAttribute("data-bs-target", "#heroCarousel");
      indicator.setAttribute("data-bs-slide-to", index);

      if (index === 0) {
        indicator.classList.add("active");
      }

      heroCarouselIndicators.appendChild(indicator);
    });
  }, []);

  return (
    <section id="hero">
        <div
          id="heroCarousel"
          data-bs-interval="2000"
          className="carousel slide carousel-fade"
          data-bs-ride="carousel"
        >
        <ol class="carousel-indicators" id="hero-carousel-indicators"></ol>

          <div className="carousel-inner" role="listbox">
            <div
              className="carousel-item active"
              style={{ backgroundImage: "url(assets/img/slide/slide-1.jpg)" }}
            >
              <div className="carousel-container">
                <div className="container">
                  <h2 className="animated fadeInDown">
                    Welcome to <span>Hidayah</span>
                  </h2>
                  <p className="animated fadeInUp">
                    Ut velit est quam dolor ad a aliquid qui aliquid. 
                  </p>
                  <a href="#about" className="btn-get-started scrollto">
                    <i className="bx bx-chevrons-down"></i>
                  </a>
                </div>
              </div>
            </div>

            <div
              className="carousel-item"
              style={{ backgroundImage: "url(assets/img/slide/slide-2.jpg)" }}
            >
              <div className="carousel-container">
                <div className="container">
                  <h2 className="animated fadeInDown">Lorem Ipsum Dolor</h2>
                  <p className="animated fadeInUp">
                    Ut velit est quam dolor ad a aliquid qui aliquid.
                  </p>
                  <a href="#about" className="btn-get-started scrollto">
                    <i className="bx bx-chevrons-down"></i>
                  </a>
                </div>
              </div>
            </div>

            <div
              className="carousel-item"
              style={{ backgroundImage: "url(assets/img/slide/slide-3.jpg)" }}
            >
              <div className="carousel-container">
                <div className="container">
                  <h2 className="animated fadeInDown">
                    Sequi ea ut et est quaerat
                  </h2>
                  <p className="animated fadeInUp">
                    Ut velit est quam dolor ad a aliquid qui aliquid.
                  </p>
                  <a href="#about" className="btn-get-started scrollto">
                    <i className="bx bx-chevrons-down"></i>
                  </a>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
  );
}

export default Slider;
