/* eslint-disable react/jsx-no-target-blank */
/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState } from "react";
import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import NavDropdown from "react-bootstrap/NavDropdown";

import { Link } from "react-router-dom";

function Header() {
  const [expanded, setExpanded] = useState(false);

  const toggleNav = () => {
    setExpanded(expanded ? false : "expanded");
  };

  const handleClick = () => {
    window.scrollTo(0, 0);
  };

  return (
    <Navbar
      id="header"
      sticky="top"
      expand="lg"
      expanded={expanded}
      collapseOnSelect
    >
      <Container className="container d-flex align-items-center justify-content-center">
        <Link to="/" className="logo">
          <img
            src="assets/img/Siyah Logo.webp"
            alt="Siyah Logo"
            className="img-fluid"
          />
        </Link>
        <h1 className="logo me-auto">
          <Link to="/"></Link>
        </h1>

        <Navbar.Toggle onClick={() => {toggleNav();}} aria-controls="basic-navbar-nav" />
        <Navbar.Collapse
          expanded={expanded}
          className="justify-content-center"
          id="basic-navbar-nav"
        >
          <Nav className="ms-auto" id="navbar">
            <Nav.Link
              as={Link}
              to="/"
              onClick={() => {toggleNav(); handleClick();}}
              className="nav-link scrollto active"
            >
              Anasayfa
            </Nav.Link>
            <Nav.Link
              as={Link}
              to="/about"
              className="nav-link scrollto"
              onClick={() => {toggleNav(); handleClick();}}
            >
              Hakkımda
            </Nav.Link>

            <NavDropdown
              title="Hizmetlerimiz"
              id="collasible-nav-dropdown"
              class="nav-link scroolto"
            >
              <NavDropdown.Item
                as={Link}
                to="/Microblading"
                onClick={() => {toggleNav(); handleClick();}}
              >
                Microblading
              </NavDropdown.Item>
              <NavDropdown.Item as={Link} to="/Pudralama" onClick={() => {toggleNav(); handleClick();}}>
                Pudralama Kaş Tekniği
              </NavDropdown.Item>
              <NavDropdown.Item
                as={Link}
                to="/EyelineDipliner"
                onClick={() => {toggleNav(); handleClick();}}
              >
                Kalıcı Eyeliner-Dipliner
              </NavDropdown.Item>
              <NavDropdown.Item
                as={Link}
                to="/DudakRenklendirme"
                onClick={() => {toggleNav(); handleClick();}}
              >
                Kalıcı Dudak Renklendirme
              </NavDropdown.Item>
              <NavDropdown.Item as={Link} to="/MakyajSilme" onClick={() => {toggleNav(); handleClick();}}>
                Kalıcı Makyaj Silme İşlemi
              </NavDropdown.Item>
              <NavDropdown.Item as={Link} to="/BBGlow" onClick={() => {toggleNav(); handleClick();}}>
                BB Glow
              </NavDropdown.Item>
              <NavDropdown.Item as={Link} to="/KasKirpik" onClick={() => {toggleNav(); handleClick();}}>
                Kaş-Kirpik Laminasyonu
              </NavDropdown.Item>
            </NavDropdown>

            <NavDropdown title="Eğitimlerimiz" id="collasible-nav-dropdown">
              <NavDropdown.Item
                as={Link}
                to="/educationmicroblading"
                onClick={() => {toggleNav(); handleClick();}}
              >
                Microblading ve <br />
                Pudralama Tekniği Eğitimi
              </NavDropdown.Item>
              <NavDropdown.Item
                as={Link}
                to="educationeyelinerDipliner"
                onClick={() => {toggleNav(); handleClick();}}
              >
                Kalıcı Eyeliner - Dipliner Eğitimi
              </NavDropdown.Item>
              <NavDropdown.Item
                as={Link}
                to="/educationdudakRenklendirme"
                onClick={() => {toggleNav(); handleClick();}}
              >
                Kalıcı Dudak Renklendirme Eğitimi
              </NavDropdown.Item>
              <NavDropdown.Item
                as={Link}
                to="/educationkassilme"
                onClick={() => {toggleNav(); handleClick();}}
              >
                Kaş Silme Eğitimi
              </NavDropdown.Item>
              <NavDropdown.Item
                as={Link}
                to="/educationkaskirpiklifting"
                onClick={() => {toggleNav(); handleClick();}}
                class="nav-link scroolto"
              >
                Kaş - Kirpik Lifting Eğitimi
              </NavDropdown.Item>
            </NavDropdown>

            <Nav.Link
              as={Link}
              to="/contact"
              className="nav-link scrollto"
              onClick={() => {toggleNav(); handleClick();}}
            >
              İletişim
            </Nav.Link>
            <Nav.Link
              href="https://wa.me/90532162"
              title="Bize Whatsapptan Ulaşın"
              target={"_blank"}
              className="social-links"
            >
              <i class="bx bxl-whatsapp" style={{ fontSize: "24px" }}></i>
            </Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default Header;
