import React from "react";
import { Container, Row, Col, Button } from "react-bootstrap";
import {
  FiFacebook,
  FiInstagram,
  FiPhoneCall,
  FiTwitter,
  FiYoutube,
} from "react-icons/fi";

const TopBar = () => {
  return (
    <div className="topbar">
        <Container>
          <Button
            onClick={() => {
              changeLanguage("tr");
            }}
          >
            {t("TR")}&nbsp;
            <img
              src="/assets/img/TR Bayragi.webp"
              alt=""
              width="20"
              height="17"
            />
          </Button>
          <Button
            onClick={() => {
              changeLanguage("en");
            }}
          >
            {t("EN")}&nbsp;
            <img
              src="/assets/img/EN Bayragi.webp"
              alt=""
              width="20"
              height="17"
            />
          </Button>
        </Container>
      </div>
  );
};

export default TopBar;

