/* eslint-disable jsx-a11y/anchor-is-valid */
/* eslint-disable jsx-a11y/anchor-has-content */
import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Container, Nav, Navbar, NavDropdown } from "react-bootstrap";

function Header() {
  const [expanded, setExpanded] = useState(false);

  const toggleNav = () => {
    setExpanded(expanded ? false : "expanded");
  };

  const handleClick = () => {
    window.scrollTo(0, 0);
  };

  return (
    <>
      <header className="main-header-three">
        <div className="main-header-three__top">
          <div className="main-header-three__top-inner">
            <div className="main-header-three__top-left">
              <ul className="list-unstyled main-header-three__contact-list">
                <li>
                  <div className="icon">
                    <i className="fas fa-envelope"></i>
                  </div>
                  <div className="text">
                    <p>
                      <a href="mailto:needhelp@company.com">
                        needhelp@company.com
                      </a>
                    </p>
                  </div>
                </li>
                <li>
                  <div className="icon">
                    <i className="fas fa-map-marker"></i>
                  </div>
                  <div className="text">
                    <p>30 Broklyn Golden Street. New York</p>
                  </div>
                </li>
              </ul>
            </div>
            <div className="main-header-three__top-right">
              <ul className="list-unstyled main-header-three__top-menu">
                <li>
                <Link to="/about">Hakkımızda</Link>
                </li>
                <li>
                  <Link to="/contact">İletişim</Link>
                </li>
              </ul>
              <div className="main-header-three__social-box">
                <h4 className="main-header-three__social-title">Follow on:</h4>
                <div className="main-header-three__social">
                  <a href="#">
                    <i className="fab fa-twitter"></i>
                  </a>
                  <a href="#">
                    <i className="fab fa-facebook"></i>
                  </a>
                  <a href="#">
                    <i className="fab fa-pinterest-p"></i>
                  </a>
                  <a href="#">
                    <i className="fab fa-instagram"></i>
                  </a>
                </div>
              </div>
            </div>
          </div>
        </div>
        <Navbar
          expand="lg"
          expanded={expanded}
          className={`main-menu main-menu-three ${
            expanded ? "stricked-menu" : ""
          }`}
          bg="light"
          variant="light"
          fixed="top"
          sticky="top"
          style={{ zIndex: 1000 }}
        >
          <Container className="container d-flex ">
            <Navbar.Brand as={Link} to="/">
              <img
                src="assets/images/resources/logo-2.png"
                alt="Logo"
                className="main-menu-three__logo"
              />
            </Navbar.Brand>
            <Navbar.Toggle
              aria-controls="navbarNav"
              onClick={() => {
                toggleNav();
                handleClick();
              }}
              className="mobile-nav__toggler"
            >
              <i className="fa fa-bars"></i>
            </Navbar.Toggle>
            <Navbar.Collapse id="navbarNav">
              <Nav className="ms-auto">
                <Nav.Link
                  as={Link}
                  to="/"
                  onClick={() => {
                    toggleNav();
                    handleClick();
                  }}
                >
                  Home
                </Nav.Link>
                <NavDropdown
                  title="Pages"
                  id="pages-dropdown"
                  className="current megamenu"
                >
                  <NavDropdown.Item
                    as={Link}
                    to="/about"
                    onClick={() => {
                      toggleNav();
                      handleClick();
                    }}
                  >
                    About
                  </NavDropdown.Item>
                  <NavDropdown.Item
                    as={Link}
                    to="/services"
                    onClick={() => {
                      toggleNav();
                      handleClick();
                    }}
                  >
                    Services
                  </NavDropdown.Item>
                  <NavDropdown.Item
                    as={Link}
                    to="/projects"
                    onClick={() => {
                      toggleNav();
                      handleClick();
                    }}
                  >
                    Projects
                  </NavDropdown.Item>
                  <NavDropdown.Item
                    as={Link}
                    to="/blog"
                    onClick={() => {
                      toggleNav();
                      handleClick();
                    }}
                  >
                    Blog
                  </NavDropdown.Item>
                </NavDropdown>
                <Nav.Link
                  as={Link}
                  to="/about"
                  onClick={() => {
                    toggleNav();
                    handleClick();
                  }}
                >
                  About
                </Nav.Link>
                <Nav.Link
                  as={Link}
                  to="/"
                  onClick={() => {
                    toggleNav();
                    handleClick();
                  }}
                >
                  Product
                </Nav.Link>
                <Nav.Link
                  as={Link}
                  to="/"
                  onClick={() => {
                    toggleNav();
                    handleClick();
                  }}
                >
                  Contact
                </Nav.Link>
              </Nav>
            </Navbar.Collapse>
          </Container>
        </Navbar>
        <div class="stricky-header stricked-menu main-menu main-menu-three">
          <div class="sticky-header__content"></div>
        </div>
      </header>
    </>
  );
}

export default Header;
