/* eslint-disable react/jsx-no-target-blank */
/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState } from "react";
import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import { Link } from "react-router-dom";

function Header() {
  const [expanded, setExpanded] = useState(false);

  const toggleNav = () => {
    setExpanded(expanded ? false : "expanded");
  };

  const handleClick = () => {
    window.scrollTo(0, 0);
  };

  return (
    <Navbar id="header" sticky="top" expand="lg" expanded={expanded}>
      <Container className="container d-flex align-items-center justify-content-center">
        <Link
          to="/"
          onClick={() => {
            toggleNav();
          }}
          className="logo"
        >
          <img
            src="assets/img/Aypinautomat/aypin 1.webp"
            alt="Aypin Logo"
            className="img-fluid"
          />
        </Link>
        <h1 className="logo me-auto">
          <Link
            to="/"
            onClick={() => {
              toggleNav();
              handleClick();
            }}
          ></Link>
        </h1>
        <Navbar.Brand
          as={Link}
          to="/"
          onClick={() => {
            toggleNav();
            handleClick();
          }}
        ></Navbar.Brand>
        <Navbar.Toggle
          aria-controls="basic-navbar-nav"
          onClick={() => {
            toggleNav();
          }}
        />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto" id="navbar">
            <Nav.Link
              as={Link}
              to="/"
              onClick={() => {
                toggleNav();
                handleClick();
              }}
              className="nav-link scrollto active"
            >
              Anasayfa
            </Nav.Link>
            <Nav.Link
              as={Link}
              to="/aboutus"
              onClick={() => {
                toggleNav();
                handleClick();
              }}
              className="nav-link scrollto"
            >
              Hakkımızda
            </Nav.Link>
            <li class="dropdown">
              <Link
                as={Link}
                to="/portfolio"
                onClick={() => {
                  toggleNav();
                  handleClick();
                }}
              >
                <span>Ürünlerimiz</span>
                <i class="bi bi-chevron-down" style={{ fontSize: "12px" }}></i>
              </Link>
              <ul>
                <li>
                  <Link
                    as={Link}
                    to="#"
                    onClick={() => {
                      toggleNav();
                      handleClick();
                    }}
                  >
                    Drop Down 1
                  </Link>
                </li>
                <li>
                  <Link
                    as={Link}
                    to="#"
                    onClick={() => {
                      toggleNav();
                      handleClick();
                    }}
                  >
                    Drop Down 2
                  </Link>
                </li>
                <li>
                  <Link
                    as={Link}
                    to="#"
                    onClick={() => {
                      toggleNav();
                      handleClick();
                    }}
                  >
                    Drop Down 3
                  </Link>
                </li>
                <li>
                  <Link
                    as={Link}
                    to="#"
                    onClick={() => {
                      toggleNav();
                      handleClick();
                    }}
                  >
                    Drop Down 4
                  </Link>
                </li>
              </ul>
            </li>

            <Nav.Link
              as={Link}
              to="/contact"
              onClick={() => {
                toggleNav();
                handleClick();
              }}
              className="nav-link scrollto"
            >
              İletişim
            </Nav.Link>

            <Nav.Link
              href="https://wa.me/905321621647"
              title="Bize Whatsapptan Ulaşın"
              target="_blank"
            >
              <i class="bx bxl-whatsapp"></i>
            </Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default Header;
