import React, { useEffect } from "react";
import Carousel from "react-bootstrap/Carousel";

function Slider() {
  useEffect(() => {
    const heroCarouselIndicators = document.querySelector(
      "#heroCarouselIndicators"
    );
    const heroCarouselItems = document.querySelectorAll(
      "#heroCarousel .carousel-item"
    );

    heroCarouselItems.forEach((item, index) => {
      const indicator = document.createElement("li");
      indicator.classList.add("carousel-indicator");
      indicator.setAttribute("data-bs-target", "#heroCarousel");
      indicator.setAttribute("data-bs-slide-to", index);

      if (index === 0) {
        indicator.classList.add("active");
      }

      heroCarouselIndicators.appendChild(indicator);
    });
  }, []);

  return (
    <section id="hero">
      <div
        id="heroCarousel"
        data-bs-interval="2000"
        className="carousel slide carousel-fade"
        data-bs-ride="carousel"
      >
        <ol className="carousel-indicators" id="heroCarouselIndicators"></ol>

        <Carousel className="carousel-inner" role="listbox">
          <Carousel.Item
            className="carousel-item active"
            style={{ backgroundImage: "url(assets/img/Aypinautomat/A3.webp)" }}
          >
            <Carousel.Caption className="carousel-container">
              <div className="container">
                <h2 className="animated fadeInDown">AYPİN AUTOMAT</h2>
                <a href="#about" className="btn-get-started scrollto">
                  <i className="bx bx-chevrons-down"></i>
                </a>
              </div>
            </Carousel.Caption>
          </Carousel.Item>

          <Carousel.Item
            className="carousel-item"
            style={{ backgroundImage: "url(assets/img/Aypinautomat/A1.webp)" }}
          >
            <div className="carousel-container">
              <div className="container">
                <h2 className="animated fadeInDown">AYPİN AUTOMAT</h2>
                <a href="#about" className="btn-get-started scrollto">
                  <i className="bx bx-chevrons-down"></i>
                </a>
              </div>
            </div>
          </Carousel.Item>

          <Carousel.Item
            className="carousel-item"
            style={{ backgroundImage: "url(assets/img/Aypinautomat/6.webp)" }}
          >
            <div className="carousel-container">
              <div className="container">
                <h2 className="animated fadeInDown">AYPİN AUTOMAT</h2>
                <a href="#about" className="btn-get-started scrollto">
                  <i className="bx bx-chevrons-down"></i>
                </a>
              </div>
            </div>
          </Carousel.Item>
        </Carousel>
      </div>
    </section>
  );
}

export default Slider;
