import React, { useState } from "react";
import { Container, Nav, Navbar, NavDropdown } from "react-bootstrap";
import { Link } from "react-router-dom";

function Header() {
  const [expanded, setExpanded] = useState(false);

  const toggleNav = () => {
    setExpanded(expanded ? false : "expanded");
  };

  const handleClick = () => {
    window.scrollTo(0, 0);
  };

  return (
    <Navbar id="header" className="fixed-top header-inner-pages" expand="lg">
      <Container className="container d-flex align-items-center justify-content-center">
      <Link to="/" className="logo">
          <img
            src="assets/img/Aypinautomat/aypin 1.webp"
            alt="Siyah Logo"
            className="img-fluid"
          />
        </Link>
        <h1 className="logo me-auto">
          <Link to="/"></Link>
        </h1>
        <Navbar.Toggle
          aria-controls="navbar"

          onClick={toggleNav}
        />
        <Navbar.Collapse id="navbar" className={`${expanded ? "show" : ""}`}>
          <Nav className="me-auto">
            <Nav.Link className="nav-link scrollto active" href="#hero">
              Home
            </Nav.Link>
            <Nav.Link className="nav-link scrollto" href="#about">
              About
            </Nav.Link>
            <Nav.Link className="nav-link scrollto" href="#services">
              Services
            </Nav.Link>
            <Nav.Link className="nav-link scrollto" href="#portfolio">
              Portfolio
            </Nav.Link>
            <Nav.Link className="nav-link scrollto" href="#team">
              Team
            </Nav.Link>
            <NavDropdown title="Drop Down" id="dropdown">
              <NavDropdown.Item href="#">Drop Down 1</NavDropdown.Item>
              <NavDropdown.Item href="#">
                <span>Deep Drop Down</span>{" "}
                <i className="bi bi-chevron-right"></i>
              </NavDropdown.Item>
              <NavDropdown.Item href="#">Drop Down 2</NavDropdown.Item>
              <NavDropdown.Item href="#">Drop Down 3</NavDropdown.Item>
              <NavDropdown.Item href="#">Drop Down 4</NavDropdown.Item>
            </NavDropdown>
            <Nav.Link className="nav-link scrollto" href="#contact">
              Contact
            </Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default Header;
