/* eslint-disable react/jsx-no-target-blank */
import React, { useState } from "react";
import { Button, Container, Nav, Navbar, NavDropdown } from "react-bootstrap";
import { Link } from "react-router-dom";
import { useTranslation } from "react-i18next";

function Header() {
  const { t, i18n } = useTranslation();
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };
  const [expanded, setExpanded] = useState(false);

  const toggleNav = () => {
    setExpanded(!expanded);
  };

  const handleClick = () => {
    window.scrollTo(0, 0);
    setExpanded(false);
  };

  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
  };

  return (
    <header className="main-header sticky-top fixed-top">
      <div
        className="topbar fixed-top sticky-top"
        style={{ backgroundColor: "#161617", padding: "10px" }}
      >
        <Container>
          <div className="d-flex justify-content-between align-items-center">
            <div className="d-flex align-items-center">
              <span className="topbar__wrapper">
                <ul className="list-unstyled topbar__list">
                  <li>
                    <span className="fas fa-envelope"></span>
                    <a href="mailto:info@bkbsolarlight.com">
                      info@bkbsolarlight.com
                    </a>
                  </li>
                </ul>
              </span>
            </div>
            <div className="d-flex">
              <Button
                style={{
                  backgroundColor: "#f9d21c",
                  padding: "3px 7px 3px 7px",
                  fontSize: "13px",
                }}
                onClick={() => changeLanguage("tr")}
              >
                TR
              </Button>
              &nbsp;
              <Button
                style={{
                  backgroundColor: "#f9d21c",
                  padding: "3px 7px 3px 7px",
                  fontSize: "13px",
                }}
                onClick={() => changeLanguage("en")}
              >
                EN
              </Button>
              &nbsp;
              <Button
                style={{
                  backgroundColor: "#f9d21c",
                  padding: "3px",
                  fontSize: "13px",
                }}
                onClick={() => changeLanguage("ar")}
              >
                عربي
              </Button>
            </div>
          </div>
        </Container>
      </div>
      <Navbar
        collapseOnSelect
        expand="lg"
        bg="light"
        variant="light"
        className="main-menu pt-2"
        expanded={expanded}
      >
        <Container fluid>
          <Navbar.Brand>
            <Link to="/" className="main-menu__logo" onClick={handleClick}>
              <img
                src="assets/images/favicons/250x150.png"
                width="126"
                height="84"
                alt="BKB"
              />
            </Link>
          </Navbar.Brand>

          <Navbar.Toggle
            aria-controls="responsive-navbar-nav"
            className="main-menu__toggler mobile-nav__toggler"
            onClick={toggleNav}
          />

          <div class="main-menu__nav ms-auto ">
            <ul class="main-menu__list">
              <li>
                <a href="/"></a>
              </li>
              <li>
                <a href="index.html">Anasayfa</a>
              </li>
              <li>
                <a href="about.html">Hakkımızda</a>
              </li>
              <li class="dropdown">
                <a href="products.html">Ürünlerİmİz</a>
                <ul class="sub-menu">
                  <li class="dropdown">
                    <a href="#">Ürünler 1</a>
                    <ul class="sub-menu">
                      <li><a href="products.html">Ürün 1</a></li>
                      <li><a href="products-left.html">Ürün 2</a></li>
                      <li><a href="products-right.html">Ürün 3</a></li>
                    </ul>
                  </li>
                  <li>
                    <a href="products-carousel.html">Ürünler 2</a>
                  </li>
                  <li><a href="product-details.html">Ürünler 3</a></li>
                  <li><a href="cart.html">Ürünler 4</a></li>
                  <li><a href="checkout.html">Ürünler 5</a></li>
                </ul>
              </li>
              <li><a href="contact.html">İLETİŞİM</a></li>
            </ul>
          </div>
        </Container>
      </Navbar>
    </header>
  );
}

export default Header;
