import React from "react";
import { Link } from "react-router-dom";

function HizmetCardTitle(props) {
  const { picture, title, nav, desc } = props;
  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  };
  return (
    <>
      <figure className="figure">
        <Link to={picture}>
          <img
            src={picture}
            className="figure-img img-fluid rounded shadow"
            alt={desc}
          />
        </Link>
        <Link to={nav} onClick={scrollToTop}>
          {title}
        </Link>
      </figure>
    </>
  );
}

export default HizmetCardTitle;
